function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var cep = g_form.getValue('cep');
    alert(cep);
    var buscaCep = new GlideAjax("UtilsNowPro");
    buscaCep.addParam('sysparm_name', 'buscarCep');
    buscaCep.addParam('sysparm_cep', cep);
    buscaCep.getXML(retornoAjax);

    function retornoAjax(response) {

        var answer = response.responseXML.documentElement.getAttribute("answer");
        //  alert(answer);
        if (answer == true) {
            g_form.addErrorMessage("CEP invÃ¡lido");
        } else {
            var info = JSON.parse(answer);
            // alert(info.cidade);
            g_form.setValue("endereco", info.logradouro);
            g_form.setValue("bairro", info.bairro);
            g_form.setValue("cidade", info.localidade);
            g_form.setValue("estado", info.uf);

        }

    }
    //Type appropriate comment here, and begin script below

}
