catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    g_form.clearValue('resultado');
    var valor = newValue;
    var moeda1 = g_form.getDisplayValue('moeda_1');
    var moeda2 = g_form.getDisplayValue('moeda_2');

    if (moeda1 == '' || moeda2 == '') {
        g_form.addErrorMessage("Para conversão, os campos de moeda devem ser preenchidos.");
        g_form.setValue("valor_a_ser_preenchido", '');
        g_form.setValue("resultado", '');
    } else {
        var cotacao = new GlideAjax("UtilsNowPro");
        cotacao.addParam('sysparm_name', 'cotacao');
        cotacao.addParam('sysparm_moeda1', moeda1);
        cotacao.addParam('sysparm_moeda2', moeda2);
        cotacao.getXML(retornoAJAX);

        function retornoAJAX(response) {
            var answer = response.responseXML.documentElement.getAttribute("answer");

            if (answer == 'true') {
                g_form.addErrorMessage("Cotação não encontrada");
            } else {
                var info = JSON.parse(answer);

                for (var key in info) {
                    if (info.hasOwnProperty(key)) {
                        var highValue = parseFloat(info[key].high); // Convertendo para número
                        var codein = info[key].codein;
                    }
                }

                if (!isNaN(highValue)) {
                    var calc = parseFloat(valor) * highValue;
                    g_form.setValue('resultado', calc.toFixed(2) + " " + codein);
                } else {
                    g_form.addErrorMessage("Erro ao calcular o valor. Cotação inválida.");
                }
            }
        }
    }
}
